cd /Users/ivisser/Documents/projects/depmixProject/depmixNew/rforge/depmix/trunk/srcllR CMD SHLIB lystig.csetwd("/Users/ivisser/Documents/projects/depmixProject/depmixNew/rforge/depmix/trunk/srcll")dyn.load("lystig.so")# # also look at filter!!!!!# library(depmixS4)data(speed)mod <- depmix(list(rt~1,corr~1),data=speed,nstates=2,family=list(gaussian(),multinomial()))# print the model, formulae and parameter valuesmodx1=lystig(mod@init,mod@trDens,apply(mod@dens,c(1,3),prod),mod@ntimes)x1b=lystig(mod@init,mod@trDens,apply(mod@dens,c(1,3),prod),439)source("lystig2.R")x2=lystig(mod@init,mod@trDens,apply(mod@dens,c(1,3),prod),mod@ntimes)x2b=lystig(mod@init,mod@trDens,apply(mod@dens,c(1,3),prod),439)all.equal(x1,x2)all.equal(x1b,x2b).Call("lystig",as.integer(mod@ntimes))dyn.unload("lystig.so")